import React from 'react';

import _ from 'lodash';
import Autobahn from 'autobahn-react';


/**
 * @function publishes
 * @memberof Simpl.decorators.pubsub
 */
export function publishes(Component, topic, options = {}) {
  const defaults = { acknowledge: true, disclose_me: true, exclude_me: false };
  const optionsWithDefaults = Object.assign(defaults, {}, options);


  class Publisher extends React.Component {
    constructor(props) {
      super(props);
      this.publish = this.publish.bind(this);
    }
    publish(payload) {
      const args = payload.args || [];
      const kwargs = payload.kwargs || {};
      let resolvedTopic = topic;

      if (_.isFunction(topic)) {
        resolvedTopic = topic.bind(this)(this.props);
      }
      return Autobahn.publish(resolvedTopic, args, kwargs, optionsWithDefaults)
      .then(publication => {
        if (this.props.onPublished) {
          return this.props.onPublished(
            resolvedTopic, args, kwargs, optionsWithDefaults, publication
          );
        }
        return publication;
      })
      .catch(error => {
        if (this.props.onPublishedError) {
          return this.props.onPublishedError(
            resolvedTopic, args, kwargs, optionsWithDefaults, error
          );
        }
        return error;
      });
    }
    render() {
      return <Component {...this.props} {...this.state} publish={this.publish} />;
    }
  }

  Publisher.propTypes = {
    Autobahn: React.PropTypes.object,
    onPublished: React.PropTypes.func,
    onPublishedError: React.PropTypes.func,
  };

  return Publisher;
}

export default publishes;

import React from 'react';

import { connect } from 'react-redux';
import _ from 'lodash';
import Autobahn from 'autobahn-react';


/**
 * @function subscribes
 * @memberof Simpl.decorators.pubsub
 */
export function subscribes(Component, topics, options = {}, staticMethods = {}) {
  const defaults = { match: 'prefix' };
  const optionsWithDefaults = Object.assign(defaults, {}, options);

  const mapDispatchToProps = (dispatch, ownProps) => ({
    onMessage(args, kwargs, event) {
      const store = ownProps.append === undefined ? true : ownProps.append;
      const action = store ? this.appendMessage : this.updateMessage;

      action({ args, kwargs, event });
      if (ownProps.onReceived !== undefined) {
        ownProps.onReceived(args, kwargs, event);
      }
    },
  });

  class SubscriptionContainer extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        data: [],
      };
      this.appendMessage = this.appendMessage.bind(this);
      this.updateMessage = this.updateMessage.bind(this);
    }
    componentDidMount() {
      this.subscriptions = [];
      // eslint-disable-next-line no-unused-vars
      Autobahn.Connection.onReady(([session, details]) => {
        this.subscribeTo(session);
      });
      this.subscribeTo(Autobahn);
    }
    shouldComponentUpdate(nextProps, nextState) {
      return this.props !== nextProps || this.state !== nextState;
    }
    componentWillUnmount() {
      const resolvedTopics = this.getTopics();
      console.log(`Unmounting ${resolvedTopics}`);
      this.unsubscribe();
    }
    getTopics() {
      let resolvedTopics;
      if (!_.isArray(topics)) {
        resolvedTopics = [topics];
      } else {
        resolvedTopics = [...topics];
      }
      return resolvedTopics.map((topic) => {
        if (_.isFunction(topic)) {
          return topic.bind(this)(this.props);
        }
        return topic;
      });
    }
    subscribeTo(session) {
      const handler = this.props.onMessage.bind(this);
      const resolvedTopics = this.getTopics();

      resolvedTopics.forEach((topic) => {
        session.subscribe(topic, handler, optionsWithDefaults)
        .then(subscription => {
          this.subscriptions.push(subscription);
          console.log(`Subscribed to ${topic}`);
        })
        .catch(error => {
          console.error(`Failed to auto-subscribe to a topic: ${topic}!`, error);
        });
      });
    }
    unsubscribe() {
      this.subscriptions.forEach(subscription => {
        console.log(`Unsubscribing from '${subscription.topic}'`);
        Autobahn.unsubscribe(subscription);
      });
    }
    appendMessage(message) {
      const newVar = [...this.state.data || [], message];

      this.setState({
        data: newVar,
      });
    }
    updateMessage(message) {
      this.setState({
        data: message,
      });
    }
    render() {
      return <Component {...this.props} {...this.state} />;
    }
  }

  SubscriptionContainer.propTypes = {
    append: React.PropTypes.bool,
    options: React.PropTypes.object,
    onMessage: React.PropTypes.func,
    Autobahn: React.PropTypes.object,
  };

  for (var functionName in staticMethods) {
    SubscriptionContainer[functionName] = staticMethods[functionName];
  }

  return connect(null, mapDispatchToProps)(SubscriptionContainer);
}

export default subscribes;

