const MessageActions = require('../actions/messages');
const StateActions = require('../actions/state');


const initial = {
};

const Message = function Message(payload) {
  return { args: payload[0], kwargs: payload[1], details: payload[2] };
};

const messages = recycleState(createReducer(initial, {
  [MessageActions.appendMessage](state, action) {
    const payload = action.payload;
    const variable = payload.variable;
    // eslint-disable-next-line new-cap
    const message = Message(payload);

    const newVar = [...state[variable] || [], message];

    return Object.assign({}, state, { [variable]: newVar });
  },
  [MessageActions.updateMessage](state, action) {
    const payload = action.payload;
    const variable = payload.variable;
    // eslint-disable-next-line new-cap
    const message = Message(payload);

    return Object.assign({}, state, { [variable]: message });
  },
}), `${StateActions.recycleState}`);

export default messages;

import { createReducer } from 'redux-create-reducer';
import recycleState from 'redux-recycle';

const SimplActions = require('../actions/simpl');
const StateActions = require('../actions/state');

import { popAtIndex, updateInCollection } from '../utils/collections';


const initial = {
  treeLoaded: false,
  phasesLoaded: false,
  rolesLoaded: false,
  loaded: false,
  user: {},
  current: {},
  run: [],
  runuser: [],
  world: [],
  round: [],
  scenario: [],
  period: [],
  decision: [],
  result: [],
  phase: [],
  role: [],
};

const simpl = recycleState(createReducer(initial, {
  handleError(state, action) {
    console.error(action.payload.error, action.payload.args, action.payload.kwargs);
    return state;
  },
  addChild(state, action) {
    const key = action.payload.resource_name;
    const kwargs = Object.assign({}, action.payload.data, {
      pk: action.payload.pk,
      resource_name: action.payload.resource_name,
    });

    const index = state[key].findIndex((scope) => scope.pk === action.payload.pk);
    let items;
    if (index > -1) {
      items = updateInCollection(state[key], index, kwargs);
    } else {
      items = [...state[key] || {}, kwargs];
    }
    return Object.assign({}, state, { [key]: items });
  },
  getDataTree(state, action) {
    const newState = this.addChild(state, action);
    return action.payload.children.reduce(
      (memo, child) => this.getDataTree(memo, { payload: child })
    , newState);
  },
  [SimplActions.addChild](state, action) {
    return this.addChild(state, action);
  },
  [SimplActions.removeChild](state, action) {
    const key = action.payload.resource_name;
    const index = state[key].findIndex((scope) => scope.pk === action.payload.pk);
    const updated = popAtIndex(state[key], index);
    return Object.assign({}, state, { [key]: updated });
  },
  [SimplActions.getRunUsers](state, action) {
    if (action.payload.error) {
      return this.handleError(state, action);
    }
    return action.payload.reduce((memo, child) => (
      this.addChild(memo, { payload: child })
    ), Object.assign({}, state));
  },
  [SimplActions.getDataTree](state, action) {
    if (action.payload.error) {
      return this.handleError(state, action);
    }
    let loaded = state.loaded;
    if (state.phasesLoaded) {
      loaded = true;
    }
    return Object.assign({}, this.getDataTree(Object.assign({}, state), action), {
      treeLoaded: true,
      loaded,
    });
  },
  [SimplActions.updateScope](state, action) {
    const key = action.payload.resource_name;
    const data = Object.assign({}, action.payload.data, { pk: action.payload.pk });
    const index = state[key].findIndex((scope) => scope.pk === action.payload.pk);
    if (index === -1) {
      return state;
    }
    const newCollection = updateInCollection(state[key], index, data);

    return Object.assign({}, state, { [key]: newCollection });
  },
  [SimplActions.getCurrentRunPhase](state, action) {
    const runState = this.addChild(state, { payload: action.payload.run });

    return Object.assign({}, runState, {
      current: {
        run: action.payload.run.pk,
        phase: action.payload.phase.pk,
      },
    });
  },
  [SimplActions.getUserInfo](state, action) {
    // Get the current user's info into the user namespace
    const username = action.payload;
    let role_types = new Set();
    var found_runuser;

    for(var runuser of state.runuser) {
      if(runuser.role_name) {
        role_types.add(runuser.role_name);
      }

      if (runuser.username == username) {
        found_runuser = runuser;
      }
    }

    // Remove this user's role and we're left with the "other" role names
    role_types.delete(found_runuser.role_name);
    found_runuser.other_roles = Array.from(role_types);
    return Object.assign({}, state, {user: found_runuser});
  },
  [SimplActions.getPhases](state, action) {
    let loaded = state.loaded;
    if (state.treeLoaded && state.rolesLoaded) {
      loaded = true;
    }
    return Object.assign({}, state, {
      phase: action.payload,
      phasesLoaded: true,
      loaded,
    });
  },
  [SimplActions.getRoles](state, action) {
    let loaded = state.loaded;
    if (state.treeLoaded && state.phasesLoaded) {
      loaded = true;
    }
    return Object.assign({}, state, {
      role: action.payload,
      rolesLoaded: true,
      loaded,
    });
  },
}), `${StateActions.recycleState}`);

export default simpl;

import { createStore, applyMiddleware, compose } from 'redux';
import { reduxReactRouter } from 'redux-router';

import promiseMiddleware from 'redux-promise';
import thunkMiddleware from 'redux-thunk';
import createLogger from 'redux-logger';

import createHistory from 'history/lib/createBrowserHistory';

const loggerMiddleware = createLogger();

const composeEnhancers = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;

const defaultMiddlewares = [
  applyMiddleware(promiseMiddleware),
  applyMiddleware(thunkMiddleware),
  reduxReactRouter({ createHistory }),
];

export function finalCreateStoreFactory(NODE_ENV) {
  const middlewares = [...defaultMiddlewares];
  if (NODE_ENV !== 'production' && typeof window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ === 'undefined') {
    middlewares.push(applyMiddleware(loggerMiddleware));
  }
  return composeEnhancers(
    ...middlewares
  )(createStore);
}


export default {
  finalCreateStoreFactory,
};

/**
 * @namespace actions
 * @memberof Simpl.utils
 */
import { createAction } from 'redux-actions';


export function stringify(name, arg) {
  /* eslint no-param-reassign: "off" */
  arg.toString = () => name;
  return arg;
}

/**
 *  An action that's serializable to the provided action name.
 * @typedef {function} NamedReduxAction
 * @param {string} type - A unique name for the action
 * @param {function|undefined} [action] - Logic to perform before this action
 * will make it to the reducer.
 * @property {function} toString() - return the action's name.
*/

/**
 * Shortcut function to create an action that's serializable to a string and usable
 * as an object property.
 * Use this to create an option that's usable from within a reducer.
 * @function
 * @memberof Simpl.utils.actions
 * @param {string} type - A unique name for the action
 * @param {spread} args - Additional arguments to pass to redux-actions.createAction.
 * @returns {NamedReduxAction}
 */
export function createNamedAction(type, ...args) {
  return stringify(type, createAction(type, ...args));
}

export default {
  stringify,
  createNamedAction,
};

export function updateInCollection(collection, index, updates) {
  if (index === undefined || index === null || index >= collection.length) {
    return [...collection, Object.assign({}, updates)];
  }
  return [
    ...collection.slice(0, index),
    Object.assign({}, collection[index], updates),
    ...collection.slice(index + 1),
  ];
}

export function updateCollection(collection, updates) {
  return collection.map((item) => Object.assign({}, item, updates));
}

export function popAtIndex(collection, index) {
  if (index < 0) {
    return collection.slice(0, index);
  }
  return [
    ...collection.slice(0, index),
    ...collection.slice(index + 1, collection.length),
  ];
}


export default {
  updateInCollection,
  updateCollection,
  popAtIndex,
};

