import { createStore, applyMiddleware, compose } from 'redux';
import { reduxReactRouter } from 'redux-router';

import promiseMiddleware from 'redux-promise';
import thunkMiddleware from 'redux-thunk';
import createLogger from 'redux-logger';

import createHistory from 'history/lib/createBrowserHistory';

const loggerMiddleware = createLogger();

// eslint-disable-next-line no-underscore-dangle
const composeEnhancers = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;

const defaultMiddlewares = [
  applyMiddleware(promiseMiddleware),
  applyMiddleware(thunkMiddleware),
  reduxReactRouter({ createHistory }),
];

/**
 * Adds promise, thunk, router and dev tools middlewares to your store
 *
 * @param      {string}  NODE_ENV  The node env
 * @return     {function}  a `createStore` factory
 */
export function finalCreateStoreFactory(NODE_ENV) {
  const middlewares = [...defaultMiddlewares];
  if (NODE_ENV !== 'production'
    // eslint-disable-next-line no-underscore-dangle
    && typeof window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ === 'undefined') {
    middlewares.push(applyMiddleware(loggerMiddleware));
  }
  return composeEnhancers(
    ...middlewares
  )(createStore);
}


export default {
  finalCreateStoreFactory,
};

/**
 * @namespace actions
 * @memberof Simpl.utils
 */
import { createAction } from 'redux-actions';


export function stringify(name, arg) {
  /* eslint no-param-reassign: "off" */
  arg.toString = () => name;
  return arg;
}

/**
 *  An action that's serializable to the provided action name.
 * @typedef {function} NamedReduxAction
 * @param {string} type - A unique name for the action
 * @param {function|undefined} [action] - Logic to perform before this action
 * will make it to the reducer.
 * @property {function} toString() - return the action's name.
*/

/**
 * Shortcut function to create an action that's serializable to a string and usable
 * as an object property.
 * Use this to create an option that's usable from within a reducer.
 * @function
 * @memberof Simpl.utils.actions
 * @param {string} type - A unique name for the action
 * @param {spread} args - Additional arguments to pass to redux-actions.createAction.
 * @returns {NamedReduxAction}
 */
export function createNamedAction(type, ...args) {
  return stringify(type, createAction(type, ...args));
}

export default {
  stringify,
  createNamedAction,
};

export function updateInCollection(collection, index, updates) {
  if (index === undefined || index === null || index >= collection.length) {
    return [...collection, Object.assign({}, updates)];
  }
  return [
    ...collection.slice(0, index),
    Object.assign({}, collection[index], updates),
    ...collection.slice(index + 1),
  ];
}

export function updateCollection(collection, updates) {
  return collection.map((item) => Object.assign({}, item, updates));
}

export function popAtIndex(collection, index) {
  if (index < 0) {
    return collection.slice(0, index);
  }
  return [
    ...collection.slice(0, index),
    ...collection.slice(index + 1, collection.length),
  ];
}


export default {
  updateInCollection,
  updateCollection,
  popAtIndex,
};

